    public void rank(){
        
        try {
            sql="select ApID from applications where JobRef='"+RefNoBx.getSelectedItem().toString()+"'";  
            
         JTable tbl = new JTable(db.query(sql));
         String[] applcnt = new String[tbl.getRowCount()];
         for(int i=0;i<applcnt.length;i++){
             applcnt[i]=tbl.getValueAt(i, 0).toString();
             System.out.println(applcnt.length);
         }
         tbmodel = db.query("select IDNUMBER,FULLNAME,EMAIL,MOBILENUMBER,GENDER,ETHNICITY,DISABILITY from userprofile");
         DefaultTableModel model=new DefaultTableModel(0, tbmodel.getColumnCount()); 
         model.setColumnIdentifiers(getCols(tbmodel));
         model.addColumn("SCORE OUT OF: "+getTotalWeight());
         model.addColumn("MATCH RESULT");
         
         //model.addColumn("RANK POSITION");
         double[] succesRate = new double[applcnt.length];
         double[] score = new double[applcnt.length];
         String[] rs = new String[applcnt.length];
         for(int i=0;i<applcnt.length;i++){
                  double results=result(applcnt[i]);
                  succesRate[i]=(results/getTotalWeight())*100; 
                  if(succesRate[i]>65.0)
                      rs[i]="+";
                  else
                      rs[i]="-";
                  score[i]=results;
                  sql = "select IDNUMBER,FULLNAME,EMAIL,MOBILENUMBER,GENDER,ETHNICITY,DISABILITY from userprofile WHERE APID='"+applcnt[i]+"'";
                  model.addRow(row(0, db.query(sql))); 
                  model.setValueAt(rs[i], i, model.getColumnCount()-1);
                  model.setValueAt(score[i], i, model.getColumnCount()-2);
             }    
             recommenderTable.setModel(model);
        } catch (SQLException ex) {
            Logger.getLogger(JobApp.class.getName()).log(Level.SEVERE, null, ex);
        }
            
    }
    
 
    private String[] row(int index,DefaultTableModel table){
        String[] row = new String[table.getColumnCount()];
        
          
        if(row.length>1)
        for(int i=0;i<row.length;i++){
            String val=table.getValueAt(index, i).toString();
            if(val==null)
                val="none";
            row[i]=val;
        }  
       
     
        
        return row;
    }
    private String[] getCols(DefaultTableModel model){
        String[] cols = new String[model.getColumnCount()];
        for(int i=0;i<cols.length;i++)
            cols[i]=model.getColumnName(i);
        
        return cols;
    }
